import { useState } from "react";
import { Button } from "@/components/ui/button";

interface OnboardingProps {
  onComplete: () => void;
}

export default function Onboarding({ onComplete }: OnboardingProps) {
  const [currentStep, setCurrentStep] = useState(1);

  const nextStep = () => {
    if (currentStep < 3) {
      setCurrentStep(currentStep + 1);
    } else {
      onComplete();
    }
  };

  const steps = [
    {
      title: "WHAT ARE YOU CRAVING?",
      description: "Discover recipes based on ingredients you already have at home",
      image: "https://images.unsplash.com/photo-1512621776951-a57141f2eefd?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&h=300"
    },
    {
      title: "SWIPE TO COOK",
      description: "Swipe through ingredients to create your perfect dish instantly",
      image: "https://images.unsplash.com/photo-1556909114-f6e7ad7d3136?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&h=300"
    },
    {
      title: "AI-POWERED RECIPES",
      description: "Get personalized recipe suggestions based on your preferences",
      image: "https://images.unsplash.com/photo-1577219491135-ce391730fb2c?ixlib=rb-4.0.3&auto=format&fit=crop&w=400&h=300"
    }
  ];

  const currentStepData = steps[currentStep - 1];

  return (
    <div className="fixed inset-0 bg-white z-40">
      <div className="h-full flex flex-col">
        <div className="flex-1 flex flex-col items-center justify-center p-8">
          <div className="text-center mb-8">
            <img 
              src={currentStepData.image} 
              alt={currentStepData.description}
              className="w-80 h-60 object-cover rounded-2xl shadow-lg mb-6"
            />
          </div>
          <h2 className="text-3xl font-bold text-gray-800 mb-4 text-center">
            {currentStepData.title}
          </h2>
          <p className="text-gray-600 text-center px-4 leading-relaxed">
            {currentStepData.description}
          </p>
        </div>

        <div className="p-8">
          <div className="flex justify-center mb-6">
            <div className="flex space-x-2">
              {[1, 2, 3].map((step) => (
                <div
                  key={step}
                  className={`w-3 h-3 rounded-full ${
                    step === currentStep ? 'bg-cravii-red' : 'bg-gray-300'
                  }`}
                />
              ))}
            </div>
          </div>
          <button 
            onClick={nextStep}
            className="w-full bg-cravii-red hover:bg-cravii-red-dark text-white py-4 rounded-xl font-semibold text-lg transition-colors cursor-pointer"
            type="button"
          >
            {currentStep === 3 ? 'Start Cooking' : 'Next'}
          </button>
        </div>
      </div>
    </div>
  );
}

mobile/src/App.tsx.content:
import React, { useState, useEffect } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import { StatusBar, SafeAreaView } from 'react-native';
import { GestureHandlerRootView } from 'react-native-gesture-handler';

// Import screens
import LoadingScreen from './screens/LoadingScreen';
import OnboardingScreen from './screens/OnboardingScreen';
import AuthScreen from './screens/AuthScreen';
import RegistrationScreen from './screens/RegistrationScreen';
import DietaryRestrictionsScreen from './screens/DietaryRestrictionsScreen';
import MainTabNavigator from './navigation/MainTabNavigator';
import IngredientSwipeScreen from './screens/IngredientSwipeScreen';
import RecipeResultsScreen from './screens/RecipeResultsScreen';
import RecipeDetailScreen from './screens/RecipeDetailScreen';

// Types
type AppState = 
  | "loading"
  | "onboarding"
  | "auth"
  | "registration"
  | "dietary-restrictions"
  | "main"
  | "ingredient-swipe"
  | "recipe-results"
  | "recipe-detail";

export interface User {
  id: number;
  email: string;
  dietaryRestrictions: string[];
  religionDietaryRestrictions: string[];
}

export interface UserPreferences {
  course: string;
  taste: string;
  prepTime: number;
  appliances: string[];
}

export interface Recipe {
  id: string;
  title: string;
  description: string;
  image: string;
  readyInMinutes: number;
  servings: number;
  ingredients: string[];
  instructions: string[];
  difficulty: "easy" | "medium" | "hard";
  tags: string[];
  sourceUrl?: string;
  rating?: number;
  missingIngredients?: string[];
  completenessScore?: number;
}

const Stack = createStackNavigator();

const App: React.FC = () => {
  const [appState, setAppState] = useState<AppState>("loading");
  const [user, setUser] = useState<User | null>(null);
  const [userPreferences, setUserPreferences] = useState<UserPreferences | null>(null);
  const [selectedIngredients, setSelectedIngredients] = useState<string[]>([]);
  const [selectedRecipe, setSelectedRecipe] = useState<Recipe | null>(null);

  useEffect(() => {
    // Initialize app
    const initializeApp = async () => {
      // Simulate loading time
      await new Promise(resolve => setTimeout(resolve, 2000));

      // Check if user is already logged in (implement your auth logic here)
      const savedUser = null; // Load from AsyncStorage

      if (savedUser) {
        setUser(savedUser);
        setAppState("main");
      } else {
        setAppState("onboarding");
      }
    };

    initializeApp();
  }, []);

  const handleAuth = (userData: User) => {
    setUser(userData);
    setAppState("dietary-restrictions");
  };

  const handlePreferences = (preferences: UserPreferences) => {
    setUserPreferences(preferences);
    setAppState("main");
  };

  const handleStartSwiping = () => {
    setSelectedIngredients([]);
    setAppState("ingredient-swipe");
  };

  const handleIngredientSelection = (ingredients: string[]) => {
    setSelectedIngredients(ingredients);
    setAppState("recipe-results");
  };

  const handleSelectRecipe = (recipe: Recipe) => {
    setSelectedRecipe(recipe);
    setAppState("recipe-detail");
  };

  const handleBackToMain = () => {
    setAppState("main");
  };

  const getInitialRouteName = () => {
    switch (appState) {
      case "loading": return "Loading";
      case "onboarding": return "Onboarding";
      case "auth": return "Auth";
      case "registration": return "Registration";
      case "dietary-restrictions": return "DietaryRestrictions";
      case "main": return "MainTabs";
      case "ingredient-swipe": return "IngredientSwipe";
      case "recipe-results": return "RecipeResults";
      case "recipe-detail": return "RecipeDetail";
      default: return "Loading";
    }
  };

  return (
    <GestureHandlerRootView style={{ flex: 1 }}>
      <SafeAreaView style={{ flex: 1 }}>
        <StatusBar barStyle="light-content" backgroundColor="#DC2626" />
        <NavigationContainer>
          <Stack.Navigator 
            initialRouteName={getInitialRouteName()}
            screenOptions={{ headerShown: false }}
          >
            <Stack.Screen name="Loading" component={LoadingScreen} />
            <Stack.Screen name="Onboarding">
              {() => <OnboardingScreen onComplete={() => setAppState("auth")} />}
            </Stack.Screen>
            <Stack.Screen name="Auth">
              {() => <AuthScreen 
                onLogin={handleAuth} 
                onSwitchToRegister={() => setAppState("registration")} 
              />}
            </Stack.Screen>
            <Stack.Screen name="Registration">
              {() => <RegistrationScreen 
                onRegister={handleAuth}
                onSwitchToLogin={() => setAppState("auth")} 
              />}
            </Stack.Screen>
            <Stack.Screen name="DietaryRestrictions">
              {() => <DietaryRestrictionsScreen 
                user={user!}
                onComplete={handlePreferences} 
              />}
            </Stack.Screen>
            <Stack.Screen name="MainTabs">
              {() => <MainTabNavigator 
                user={user!}
                userPreferences={userPreferences!}
                onStartSwiping={handleStartSwiping}
              />}
            </Stack.Screen>
            <Stack.Screen name="IngredientSwipe">
              {() => <IngredientSwipeScreen 
                userPreferences={userPreferences!}
                onComplete={handleIngredientSelection}
                onBack={handleBackToMain}
              />}
            </Stack.Screen>
            <Stack.Screen name="RecipeResults">
              {() => <RecipeResultsScreen 
                ingredients={selectedIngredients}
                userPreferences={userPreferences!}
                onSelectRecipe={handleSelectRecipe}
                onBack={handleBackToMain}
              />}
            </Stack.Screen>
            <Stack.Screen name="RecipeDetail">
              {() => <RecipeDetailScreen 
                recipe={selectedRecipe!}
                onBack={() => setAppState("recipe-results")}
              />}
            </Stack.Screen>
          </Stack.Navigator>
        </NavigationContainer>
      </SafeAreaView>
    </GestureHandlerRootView>
  );
};

export default App;

