export async function seedIngredients() {
  try {
    // Clear existing ingredients
    await db.delete(ingredients);
    
    // Add default searchTerms for ingredients that don't have them
    const ingredientsWithSearchTerms = comprehensiveIngredients.map(ingredient => ({
      ...ingredient,
      searchTerms: ingredient.searchTerms || [ingredient.name.toLowerCase(), ...ingredient.tags]
    }));
    
    // Insert new ingredients
    await db.insert(ingredients).values(ingredientsWithSearchTerms);
    
    console.log(`Seeded ${ingredientsWithSearchTerms.length} ingredients successfully`);
  } catch (error) {
    console.error("Error seeding ingredients:", error);
    throw error;
  }
}

// Run the seed function if this file is executed directly
if (import.meta.url === `file://${process.argv[1]}`) {
  seedIngredients().then(() => {
    console.log("Seeding complete!");
    process.exit(0);
  }).catch((error) => {
    console.error("Seeding failed:", error);
    process.exit(1);
  });
}